// Hilo para procesar la tecla capturada
void *key_thread(void *arg)
{
    while (1)
    {
        // Bloquear el mutex antes de acceder a la variable global
        if (capturing)
        {
            if (global_key == 75)   // Tecla izquierda
            {
                MUTEXLOCK;
                gotoxy(0,0);
                der1();
                Sleep(100);
                gotoxy(0,0);
                der2();
                Sleep(100);
                gotoxy(0,0);
                der3();
                Sleep(100);
                gotoxy(0,0);
                der4();
                Sleep(100);
                gotoxy(0,0);
                der3();
                Sleep(100);
                gotoxy(0,0);
                der2();
                Sleep(100);
                gotoxy(0,0);
                MUTEXUNLOCK;
            }
            else if (global_key == 77)     // Tecla derecha
            {
                MUTEXLOCK;
                gotoxy(0,0);
                der1();
                Sleep(100);
                gotoxy(0,0);
                der2();
                Sleep(100);
                gotoxy(0,0);
                der3();
                Sleep(100);
                gotoxy(0,0);
                der4();
                Sleep(100);
                gotoxy(0,0);
                der3();
                Sleep(100);
                gotoxy(0,0);
                der2();
                Sleep(100);
                gotoxy(0,0);
                MUTEXUNLOCK;
            }
            // Reiniciar la variable global y el flag después de usarla
            global_key = 0;
        }
        Sleep(100); // Esperar un momento antes de revisar de nuevo
    }
    // Limpiar el búfer de entrada después de que el bucle principal haya terminado

    return NULL;
}

