void *funcionHiloCoche(void *arg)
{
    char key;

    pthread_t tid;

    // Inicializar el mutex
    pthread_mutex_init(&key_mutex, NULL);

    // Crear el hilo para procesar la tecla
    if (pthread_create(&tid, NULL, key_thread, NULL) != 0)
    {
        fprintf(stderr, "Error al crear el hilo\n");
        return 1;
    }

    // Leer teclas presionadas hasta que se presiona una flecha izquierda o derecha
    while (1)
    {
        while (!_kbhit()) {
        gotoxy(0,0);
        MUTEXLOCK;
        centro();
        MUTEXUNLOCK;
        Sleep(300);
        gotoxy(0,0);
        MUTEXLOCK;
        der1();
        MUTEXUNLOCK;
        Sleep(300);
        gotoxy(0,0);
        }


        // Esperar hasta que se presione una tecla
        // Leer la tecla presionada
        key = getch();
        // Si la tecla es una flecha izquierda o derecha, procesarla y salir del bucle
        if (key == -32 || key == 224)   // Detectar tecla especial
        {
            key = getch(); // Leer el código de la tecla real
            if (key == 75 || key == 77)
            {
                pthread_mutex_lock(&key_mutex);
                capturing = 0;
                // Actualizar la variable global solo si es la tecla izquierda o derecha
                global_key = key;
                // Desbloquear el mutex después de modificar la variable global
                pthread_mutex_unlock(&key_mutex);
                capturing = 1;
            }
        }
        while (_kbhit())
        {
            getch();
        }
    }
}
